// Fatal is equivalent to Print() followed by a call to os.Exit(1).
func Fatal(v ...interface{}) {
	if filelog != nil {
		filelog.Output(FATAL, fmt.Sprint(v...))
	}
	if outputConsole {
		console.Output(FATAL, fmt.Sprint(v...))
	}
	os.Exit(1)
}

// Fatalf is equivalent to Printf() followed by a call to os.Exit(1).
func Fatalf(format string, v ...interface{}) {

	if filelog != nil {
		filelog.Output(FATAL, fmt.Sprintf(format, v...))
	}
	if outputConsole {
		console.Output(FATAL, fmt.Sprintf(format, v...))
	}

	os.Exit(1)
}

// Fatalln is equivalent to Println() followed by a call to os.Exit(1).
func Fatalln(v ...interface{}) {
	if filelog != nil {
		filelog.Output(FATAL, fmt.Sprintln(v...))
	}

	if outputConsole {
		console.Output(FATAL, fmt.Sprintln(v...))
	}
	os.Exit(1)
}

// Panic is equivalent to Print() followed by a call to panic().
func Panic(v ...interface{}) {
	s := fmt.Sprint(v...)
	if filelog != nil {
		filelog.Output(PANIC, s)
	}
	if outputConsole {
		console.Output(PANIC, s)
	}

	panic(s)
}

// Panicf is equivalent to Printf() followed by a call to panic().
func Panicf(format string, v ...interface{}) {

	s := fmt.Sprintf(format, v...)
	if filelog != nil {
		filelog.Output(PANIC, s)
	}
	if outputConsole {
		console.Output(PANIC, s)
	}

	panic(s)
}

// Panicln is equivalent to Println() followed by a call to panic().
func Panicln(v ...interface{}) {

	s := fmt.Sprintln(v...)

	if filelog != nil {
		filelog.Output(PANIC, s)
	}
	if outputConsole {
		console.Output(PANIC, s)
	}

	panic(s)
}

func Stack(v ...interface{}) {
	s := fmt.Sprint(v...)
	s += "\n"
	buf := make([]byte, 1024*1024)
	n := runtime.Stack(buf, true)
	s += string(buf[:n])
	s += "\n"

	if filelog != nil {
		filelog.Output(ERROR, s)
	}
	if outputConsole {
		console.Output(ERROR, s)
	}
}
